#!/usr/bin/env python3

#creating a script that counts the numbers of A's, T's,C's and G's irregardless of case

dna_seq = ''

upper_dna_seq = dna_seq.upper()

a_count = upper_dna_seq.count('A')
c_count = upper_dna_seq.count('C')
g_count = upper_dna_seq.count('G')
t_count = upper_dna_seq.count('T')

print ("There are", a_count ,"a's", c_count,"c's", g_count, "g's", t_count,"t's")

print ("great job!")


